func:
// Se recuperan datos guardados
var datos = flow.get("datosSentinel");
if (!datos) {
    node.warn("¡Esperando datos del Sentinel...");
    return null;
}
var fecha = new Date().toISOString().slice(0, 19).replace('T', ' ');
var tabla = "lecturas"; // O 'nodered_db.lecturas' si necesitas el prefijo

var query = "INSERT INTO " + tabla + " (fecha, distancia1, distancia2, accel_z, alarma, orientacion) VALUES ";

// Aquí armamos la fila. Las comillas simples son  para los textos!
query += "('" + fecha + "', " +
         datos.dist_izq + ", " +
         datos.dist_cen + ", " +
         datos.accelZ + ", '" +
         datos.alarma + "', '" +      
         datos.orientacion + "');"; 

// Enviamos la consulta
msg.topic = query;
return msg;

// Preparamos los 5 mensajes individuales para tus gauges y textos
var msg1 = { payload: msg.payload.dist_izq };     // Salida 1: Sensor 1
var msg2 = { payload: msg.payload.dist_cen };     // Salida 2: Sensor 2
var msg3 = { payload: msg.payload.accelZ };       // Salida 3: Gráfico Z
var msg4 = { payload: msg.payload.alarma };       // Salida 4: Texto Alarma
var msg5 = { payload: msg.payload.orientacion };  // Salida 5: Texto Orientación

// Guardar en memoria 
flow.set("datosSentinel", msg.payload);

// Enviamos los mensajes a las 5 salidas visuales
return [msg1, msg2, msg3, msg4, msg5];

// Esta es la QUERY para leer los datos
// Le pedimos las últimas 10 filas ordenadas por fecha (descendente)
msg.topic = "SELECT fecha, distancia1, distancia2, accel_z, alarma, orientacion FROM lecturas ORDER BY fecha DESC LIMIT 20;";

return msg;

// 1. Obtener fecha actual (formato compatible con SQL)
var now = new Date();
var fecha = now.toISOString().slice(0, 19).replace('T', ' ');

// 2. Simular valores numéricos aleatorios
var dist_izq = Math.floor(Math.random() * 150); // 0 a 150 cm
var dist_cen = Math.floor(Math.random() * 150);

// 3. Lógica para simular si el bastón está Vertical u Horizontal
// Generamos una probabilidad: 20% de chances de que simule una caída
var esCaida = Math.random() < 0.2;

var accelZ_simulado;
var orientacion_txt;
var alarma_txt;

if (esCaida) {
    // CASO CAÍDA (Horizontal)
    accelZ_simulado = 1.5; // Valor bajo en Z (simula estar acostado)
    orientacion_txt = "Horizontal";
    alarma_txt = "ALERTA CAIDA";
} else {
    // CASO NORMAL (Vertical)
    accelZ_simulado = 9.8; // Valor de gravedad normal
    orientacion_txt = "Vertical";
    // Si hay obstáculo cerca, cambiamos la alarma, si no, todo OK
    if (dist_izq < 30 || dist_cen < 30) {
        alarma_txt = "PRECAUCION";
    } else {
        alarma_txt = "Normal";
    }
}

// 4. Armar la consulta SQL EXACTA usando tus nombres de columna
// Tabla: lecturas
// Columnas: fecha, distancia1, distancia2, accel_z, alarma, orientacion

var query = "INSERT INTO lecturas (fecha, distancia1, distancia2, accel_z, alarma, orientacion) VALUES ";

query += "('" + fecha + "', " +
    dist_izq + ", " +
    dist_cen + ", " +
    accelZ_simulado + ", '" +
    alarma_txt + "', '" +
    orientacion_txt + "');";

msg.topic = query;
return msg;
